catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }


    //CAP: Get Value's Oparation Field and Current Language
    var operation = newValue; // SYS_ID
    var language = g_lang; // 'en''|| 'pb'
    var field; //u_operation_nature || u_conterparty
    var isGold;

    g_ui_scripts.getUIScript('x_bdb_5pa_00000221.POTClientUtils').then(function(script) {
        script.isGold(operation, function(callback) { // resposta voltando na função de callback
            isGold = callback; //return true

            if (isGold) {

                field = 'u_operation_nature';
                script.getChoices(operation, language, field, function(callback) { // call Function
                    setChoices(field, language, callback); //call  Function with callback Answer


                    field = 'u_counterparty';
                    script.getChoices(operation, language, field, function(callback) {//call Fuction
                        setChoices(field, language, callback);//call Fuction with callback Answer
                    });
                });
            }

        });
    });
    // função recebendo os parametros por argumento possívelmente a ordem dos argumentos altera o resultado 
    //CAP: Get Choice Array by Operation type
    function setChoices(field, language, callback) { 

        g_form.clearOptions(field);

        if (language == 'pb') {
            g_form.addOption(field, '', '-- Nenhum(a) --');

        } else {
            g_form.addOption(field, '', '-- None --');
        }

        var arrChoices = JSON.parse(callback);

        for (var choice in arrChoices) {
            g_form.addOption(field, arrChoices[choice].value, arrChoices[choice].label);
        }
	}
    }
