catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    //CAP: Get Value's Oparation Field and Current Language
    var operation = newValue; // SYS_ID
    var language = g_lang; // 'en''|| 'pb'
    var field;
    var isGold;

    //CAP: Get Choice Array by Operation type
      g_ui_scripts.getUIScript('x_bdb_5pa_00000221.POTClientUtils').then(function(script) {
        script.isGold(operation, function(callback) {
            isGold = callback;

            function setChoices(field) {

                script.getChoices(operation, language, field, function(callback) {

                    g_form.clearOptions(field);

                    if (language == 'pb') {
                        g_form.addOption(field, '', '-- Nenhum(a) --');

                    } else {
                        g_form.addOption(field, '', '-- None --');
                    }

                    var arrChoices = JSON.parse(callback);

                    for (var choice in arrChoices) {
                        g_form.addOption(field, arrChoices[choice].value, arrChoices[choice].label);
                    }
                });
            }
            if (isGold) {

                field = 'u_operation_nature';
                setChoices(field);

                field = 'u_counterparty';
                setChoices(field);
            }
        });
    });
}
