sys_script_include.script:
var POTUtils = Class.create();
POTUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getLastRecordFiltered: function() {

        var filter = this.getParameter("sysparm_filter");
        var table = this.getParameter("sysparm_table");
        var field = this.getParameter("sysparm_field");

        var sys_id = '';

        try {

            var grFiltered = new GlideRecord(table);
            grFiltered.addEncodedQuery(filter);
            grFiltered.orderByDesc(field);
            grFiltered.setLimit(1);
            grFiltered.query();

            if (grFiltered.next()) {
                sys_id = grFiltered.sys_id.toString();
            }

        } catch (err) {
            gs.addErrorMessage('Script Include [POTUtils] Function [getLastRecordFiltered] error: ' + err);
        }

        return sys_id;
    },

    getCounterPartyChoicesGold: function() {

        try {

            var language = this.getParameter('sysparm_language');
            var query = 'name=x_bdb_5pa_00000221_pinch_operation^element=u_counterparty^language=' + language + '^valueIN' + gs.getProperty('x_bdb_5pa_00000221.pot.counterparty.gold.choices') + '^ORDERBYsequence';
            var grRecoverChoices = new GlideRecord('sys_choice');
            grRecoverChoices.addEncodedQuery(query);
            grRecoverChoices.query();
            var arr = [];
            while (grRecoverChoices.next()) {
                var obj = {
                    value: grRecoverChoices.getValue('value'),
                    label: grRecoverChoices.getValue('label')
                };
                arr.push(obj);
            }

        } catch (err) {
            gs.addErrorMessage('Script Include [POTUtils] Function [getCounterPartyChoicesGold] error: ' + err);
        }

        return JSON.stringify(arr);
    },

    getChoicesAjax: function() {

        try {

            var language = this.getParameter('sysparm_language');
            var operation = this.getParameter('sysparm_operation');
            var field = this.getParameter('sysparm_field');
            var property;

            switch (operation) {
					case gs.getProperty('x_bdb_5pa_00000221.pot.operation.gold'):
					if (field == 'u_operation_nature'){
						property = 'x_bdb_5pa_00000221.pot.operation.gold.nature.choices';
						break;
					}else{
						property = 'x_bdb_5pa_00000221.pot.operation.gold.counterparty.choices';
						break;
					}
            }

            var query = 'name=x_bdb_5pa_00000221_pinch_operation^element=' + field + '^language=' + language + '^valueIN' + gs.getProperty(property) + '^ORDERBYsequence';
            var grRecoverChoices = new GlideRecord('sys_choice');
            grRecoverChoices.addEncodedQuery(query);
            grRecoverChoices.query();
            var arr = [];
            while (grRecoverChoices.next()) {
                var obj = {
                    value: grRecoverChoices.getValue('value'),
                    label: grRecoverChoices.getValue('label')
                };
                arr.push(obj);
            }

        } catch (err) {
            gs.addErrorMessage('Script Include [POTUtils] Function [getOperationChoicesGold] error: ' + err);
        }

        return JSON.stringify(arr);
    },

    recoverOperationChoices: function() {

        try {

            var language = this.getParameter('sysparm_language');
            var query = 'name=x_bdb_5pa_00000221_pinch_operation^element=u_operation_nature^language=' + language + '^ORDERBYsequence';
            var grRecoverChoices = new GlideRecord('sys_choice');
            grRecoverChoices.addEncodedQuery(query);
            grRecoverChoices.query();
            var arr = [];
            while (grRecoverChoices.next()) {
                var obj = {
                    value: grRecoverChoices.getValue('value'),
                    label: grRecoverChoices.getValue('label')
                };
                arr.push(obj);
            }

        } catch (err) {
            gs.addErrorMessage('Script Include [POTUtils] Function [recoverOperationChoices] error: ' + err);
        }

        return JSON.stringify(arr);
    },


    recoverCounterpartyChoices: function() {

        try {

            var language = this.getParameter('sysparm_language');
            var query = 'name=x_bdb_5pa_00000221_pinch_operation^element=u_counterparty^language=' + language + '^ORDERBYsequence';
            var grRecoverChoices = new GlideRecord('sys_choice');
            grRecoverChoices.addEncodedQuery(query);
            grRecoverChoices.query();
            var arr = [];
            while (grRecoverChoices.next()) {
                var obj = {
                    value: grRecoverChoices.getValue('value'),
                    label: grRecoverChoices.getValue('label')
                };
                arr.push(obj);
            }

        } catch (err) {
            gs.addErrorMessage('Script Include [POTUtils] Function [recoverCounterpartyChoices] error: ' + err);
        }

        return JSON.stringify(arr);
    },

    getOperationsAJAX: function() {

        // CAP: Operation SysID
        var operation = this.getParameter('sysparm_operation');

        var operations = {
            isInterbankLeveling: (operation == gs.getProperty('x_bdb_5pa_00000221.pot.operation.interbankLeveling')),
            isGold: (operation == gs.getProperty('x_bdb_5pa_00000221.pot.operation.gold'))
        };

        return JSON.stringify(operations);
    },

    type: 'POTUtils'
});
