sys_ui_script.script:
(function() {
    "use strict";

    return {

        // CAP: Validate operation against sys properties registered (server side)
        getOperation: function(operation, callback) {
            var gaPOTUtils = new GlideAjax('x_bdb_5pa_00000221.POTUtils');
            gaPOTUtils.addParam('sysparm_name', 'getOperationsAJAX');
            gaPOTUtils.addParam('sysparm_operation', operation);
            gaPOTUtils.getXMLAnswer(function(response) {
                callback(JSON.parse(response));
            });
        },
        // CAP: Validate if operation is Interbank Leveling
        isInterbankLeveling: function(operation, callback) {
            this.getOperation(operation, function(answer) {
                callback(answer.isInterbankLeveling);
            });
        },
        // CAP: Validate if operation is Gold
        isGold: function(operation, callback) {
            this.getOperation(operation, function(answer) {
                callback(answer.isGold);
            });
        },
        // CAP: Get Operation choices for operaton Gold
        getChoices: function(operation, language, field, callback) {
            var gaPOTUtils = new GlideAjax('x_bdb_5pa_00000221.POTUtils');
            gaPOTUtils.addParam('sysparm_name', 'getChoicesAjax');
            gaPOTUtils.addParam('sysparm_operation', operation);
            gaPOTUtils.addParam('sysparm_language', language);
            gaPOTUtils.addParam('sysparm_field', field);
            gaPOTUtils.getXMLAnswer(function(response) { //armazena a resposta em uma função anonima que chama e passa a resposta para o callback, que devolve a resposta via arugmento
                callback(response);
            });
        },
        type: "POTClientUtils"
    };
})();
